import React, { useState, useEffect, useMemo } from 'react'

const initialValue={session:true};

export const AuthDataContext = React.createContext(initialValue);

const AuthDataProvider = props => {
  const [authData, setAuthData] = useState(initialValue);

  /* The first time the component is rendered, it tries to
   * fetch the auth data from a source, like a cookie or
   * the localStorage.
   */
  useEffect(() => {
    const currentAuthData = initialValue.session;///import from cookie
    if (currentAuthData) {
      setAuthData(currentAuthData);
      initialValue.session=true;
    }
  }, []);

  const onLogout = () => setAuthData(initialValue);

  const onLogin = newAuthData => setAuthData(newAuthData);

  const authDataValue = useMemo({ ...authData, onLogin, onLogout }, [authData]);

  return <AuthDataContext.Provider value={authDataValue} {...props} />;
};

export default AuthDataProvider;
